function getData(){$.get("https://data.cityofchicago.org/resource/suj7-cg3j.json?$select=vehicle_make_model,COUNT(vehicle_make_model)&$group=vehicle_make_model",function(e){drawChart(e)})}function drawChart(e){var t=new google.visualization.DataTable; t.addColumn("string","Vehicle_Make_Model"),t.addColumn("number","Count"),$.each(e,function(e,a){t.addRow([a.vehicle_make_model,parseFloat(a.COUNT_vehicle_make_model)])});var a={title:"City info",width:$(window).width(),height:.75*$(window).height()},i=new google.visualization.PieChart(document.getElementById("chart_div"));i.draw(t,a),chart2=i,data2=t}var chart2,data2;$(window).resize(function(){var e={title:"City info",width:$(window).width(),height:.75*$(window).height()};chart2.draw(data2,e)});